import React, {Component} from 'react';
import './App.css';
import Student from './Student/Student';

class App extends Component {
    state = {
        vak: 'AWD',
        students: [
            {naam: 'Maarten', geslaagd: 'Nee'},
            {naam: 'Jurgen', geslaagd: 'Ja'},
            {naam: 'Peter', geslaagd: 'Ja'}
        ]
    };

    corrigeerScoreHandler = () => {
        this.state.students[0].naam = 'Mike';
        this.setState({
            students: [
                {naam: 'Ann', geslaagd: 'Nee'},
                {naam: 'Els', geslaagd: 'Ja'},
                {naam: 'Peter', geslaagd: 'Nee'}
            ]
        });
    };

    render() {
        return (
            <div className="App">
                <h1>Dit is de App Titel</h1>
                <p>en een App paragraaf</p>
                <button onClick={this.corrigeerScoreHandler}>Corrigeer score</button>
                <Student naam={this.state.students[0].naam} geslaagd={this.state.students[0].geslaagd}/>
                <Student naam={this.state.students[1].naam} geslaagd={this.state.students[1].geslaagd}/>
                <Student naam={this.state.students[2].naam} geslaagd={this.state.students[2].geslaagd}/>

            </div>
        );
    }
}

export default App;
